import { ListGroup, Row, Col, Image, Ratio, ListGroupItem } from 'react-bootstrap';
import { useParams, useNavigate } from "react-router-dom";
import React, { useState, useEffect } from 'react';

import { getUser } from '../../api/apiRequest.js';

import { Button } from '@mui/material';
import { Link } from "react-router-dom";

function FollowingItem(props) {

    var placeholder = "data:image/png;base64,";
    const nullUser = {
        username: "User not really found",
        pfp: placeholder,
        bio: "",
        follower_count: 0,
        following_count: 0,
        followers: [],
        following: [],
        private: true,
        first: "",
        last: "",
        email: ""
        //topics = []
    }

    const [user, setUser] = useState(nullUser);
    const [buttonText, setButtonText] = useState("Following");
    const [following, setFollowing] = useState(true);

    const getUserApi = () => {

        getUser(props.user_id)
            .then(res => {
                /*
                const usr = formatUser(res.data);
                setUser(usr);
                console.log(usr);
                */
                console.log(res);
                setUser(res);
            })
            .catch(err => console.error(`Error: ${err}`));
    }

    useEffect(() => {
        getUserApi();
    }, []);

    const changeText = (Following) => {
        setButtonText(Following);
    }

    return (
        <ListGroupItem>
            <Row className="align-items-left">
                <Col>
                    <img style={{ width: "50px", height: "50px", borderRadius: "10px" }}
                        src={user.pfp}
                    />
                </Col>
                <Col>
                    {user.username}
                </Col>
                <Col>
                    <Button 
                        onClick={() => {
                            setButtonText(following ? "Following" : "Follow");
                            setFollowing(!following);
                        }}>
                            {buttonText}
                    </Button>
                </Col>
            </Row>
        </ListGroupItem>
    );
}

export default FollowingItem;